document.addEventListener("DOMContentLoaded",()=>{const e=document.body,t=document.querySelector("#js-burger");t&&t.addEventListener("click",()=>{e.classList.toggle("nav-open")})});const c=1;window.addEventListener("scroll",()=>{const e=window.pageYOffset||document.documentElement.scrollTop,t=document.getElementById("js-pagetop");t&&(e>c?t.classList.add("fadein"):t.classList.remove("fadein"))});const o=document.getElementById("js-pagetop");o&&o.addEventListener("click",e=>{e.preventDefault(),window.scrollTo({top:0,behavior:"smooth"})});const n=document.querySelectorAll(".sa"),s=()=>{for(let e=0;e<n.length;e++)window.innerHeight>n[e].getBoundingClientRect().top+50&&n[e].classList.add("show")};s();window.addEventListener("scroll",s);
