const n=()=>{const e=document.body,t=document.getElementById("navBtn");t&&t.addEventListener("click",()=>{e.classList.toggle("isOpen")})},s=()=>{const e=document.getElementById("scrollTopTrigger");if(!e)return;const t=1;window.addEventListener("scroll",()=>{(window.pageYOffset||document.documentElement.scrollTop)>t?e.classList.add("isShow"):e.classList.remove("isShow")}),e.addEventListener("click",o=>{o.preventDefault(),window.scrollTo({top:0,behavior:"smooth"})})},c=()=>{const e=document.querySelectorAll(".sa");if(e.length===0)return;const t=50;window.addEventListener("scroll",()=>{e.forEach(o=>{window.innerHeight>o.getBoundingClientRect().top+t&&o.classList.add("isShow")})})};document.addEventListener("DOMContentLoaded",()=>{n(),s(),c()});
