/* empty css                                */const d=()=>{const a=document.querySelector("[data-tab-nav]");if(a){const c=document.querySelectorAll("[data-tab-btn]"),n=document.querySelectorAll("[data-tab-panel]");a.addEventListener("click",o=>{const e=o.target.closest("[data-tab-btn]");if(!e)return;const s=e.dataset.tabBtn??"",r=document.getElementById(s);c.forEach(t=>t.classList.toggle("isActive",t===e)),n.forEach(t=>t.classList.toggle("isActive",t===r))})}};document.addEventListener("DOMContentLoaded",()=>{d()});
